import React, {ChangeEvent, FormEvent, ReactNode} from 'react';
import Request from '../../API/Request';
import '../../css/Inscription.css';
import DropBox from '../General/DropBox';
import Input from "../General/Input";
import SubmitButton from "../General/SubmitButton";

interface InscriptionState {
    email: string,
    password: string,
    passwordConfirmation: string,
    firstName: string,
    lastName: string,
    pseudo: string,
    validPassword: boolean,
    formWasSubmitted: boolean,
}

class Inscription extends React.Component<any,InscriptionState>{

    constructor(props: any) {
        super(props);

        this.state = {
            email: '',
            password: '',
            passwordConfirmation: '',
            firstName: '',
            lastName: '',
            pseudo: '',
            validPassword: false,
            formWasSubmitted: false,
        }


        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handlePrenomChange = this.handlePrenomChange.bind(this);
        this.handleNomChange = this.handleNomChange.bind(this);
        this.handlePseudoChange = this.handlePseudoChange.bind(this);
        this.handlePasswordConfirmationChange = this.handlePasswordConfirmationChange.bind(this);
        this.submitForm = this.submitForm.bind(this);
        this.redirectToConnection = this.redirectToConnection.bind(this);
        this.setState = this.setState.bind(this);
    }

    public handleEmailChange(event: ChangeEvent<HTMLInputElement>): void {
        // @ts-ignore
        this.setState({email: event.target.value});
    }

    public handlePasswordChange(event: ChangeEvent<HTMLInputElement>): void {
        this.setState({password: event.target.value});
    }

    public handlePasswordConfirmationChange (event: ChangeEvent<HTMLInputElement>) : void {
        this.setState(
            {passwordConfirmation: event.target.value},
            this.passwordVerification
            );
    }

    public handlePrenomChange (event: ChangeEvent<HTMLInputElement>) : void {
        this.setState({firstName: event.target.value});
    }

    public handleNomChange (event: ChangeEvent<HTMLInputElement>) : void {
        this.setState({lastName: event.target.value});
    }

    public handlePseudoChange (event: ChangeEvent<HTMLInputElement>) : void {
        this.setState({pseudo: event.target.value});
    }

    public passwordVerification () : boolean {
        let equal: boolean;
        if (this.state.passwordConfirmation.length > 8 && this.state.password === this.state.passwordConfirmation){
            equal = true;
        }
        else{
            equal = false;
        }

        return equal;
    }

    public checkForm () : boolean {
        console.log(this.passwordVerification());
        if (this.passwordVerification() && this.state.firstName !== '' && this.state.lastName !== '' && this.state.pseudo !=='' && this.state.email !== '') {
            return true;
        }
        else{
            return false;
        }
    }

    public redirectToConnection(data: any): void {
        if ('success' === data['status']) {
            // @ts-ignore
            window.location='/connexion';
        } else {
            this.setState({formWasSubmitted: true});
        }
    }

    public submitForm(event: FormEvent<HTMLFormElement> | React.MouseEvent<HTMLButtonElement, MouseEvent>): void {
        event.preventDefault();

        if (this.checkForm()){
            new Request(
                '/connection/register',
                this.redirectToConnection,
                'POST',
                {
                    prenom : this.state.firstName,
                    nom : this.state.lastName,
                    pseudo : this.state.pseudo,
                    email: this.state.email,
                    password: this.state.password,
                    confirm_password: this.state.passwordConfirmation,
                },
            );
        }
        else{
            this.setState({formWasSubmitted: true});
            alert("erreur");
        }
    }

    render(): ReactNode {
        return (
            <section className="content row inscription-bloc">
                <div className="container">
                    <div>
                        <form onSubmit={this.submitForm}>
                            <div className={"row buttons-top"}>
                                <Input id={"inputPrenom"} inputType={"text"} placeholder={"Prénom"} className={""} onChange={this.handlePrenomChange}/>
                                <Input id={"inputNom"} inputType={"text"} placeholder={"Nom"} className={""} onChange={this.handleNomChange}/>
                                <Input id={"Pseudo"} inputType={"text"} placeholder={"Pseudo"} className={""} onChange={this.handlePseudoChange}/>
                            </div>

                            <div className={"buttons-bottom"}>
                                <Input id={"inputMail"} inputType={"email"} placeholder={"Adresse email"}
                                                  className={""} onChange={this.handleEmailChange}/>
                                <Input id={"inputPassword"} inputType={"password"}
                                                  placeholder={"Mot de passe"}
                                                  className={"custom"} onChange={this.handlePasswordChange}/>
                                <Input id={"inputPasswordConfirmation"} inputType={"password"}
                                                  placeholder={"Confirmation mot de passe"}
                                                  className={"custom"} onChange={this.handlePasswordChange}/>
                                <div className={"submit-button"}>
                                    <SubmitButton
                                        text={'Inscris-toi !'}
                                        onClick={this.submitForm}
                                        className={''}
                                    />
                                </div>
                            </div>
                        </form>
                        <br/>
                        Déja Inscrit ? <a id={"redirect-button"} href={'/connexion/login'}>Connecte toi ! </a>
                    </div>
                </div>
            </section>
        )
    }
}

export default Inscription;
