import React, { useState, useEffect, useRef } from 'react'
import Nav from './Nav'
import Footer from './Footer'
import Lobby from './Lobby'
import './App.css'
import coin from './coin.svg'

const Canvas = () => {
  const canvasRef = useRef(null)
  const DIRECTIONS = {
    'up': [0, -1], 
    'down': [0, 1], 
    'left': [-1, 0],
    'right': [1, 0]
  }
  let [canvas, setCanvas] = useState(null)
  let [ctx, setCtx] = useState(null)
  let [direction, setDirection] = useState(DIRECTIONS['down'])
  const [objects] = useState([])
  const move = () => {

  }
  useEffect(() => {
    let shape = {
      width: 40,
      height: 40,
      x: 50,
      y: 100,
      radius: 50,
      color: '#FF0000'
    }
    const drawCircle = (y, x, radius) => {
      ctx.clearRect(0, 0, canvas.width, canvas.height)
      ctx.beginPath()
      ctx.arc(y, x, radius, 0, 2 * Math.PI)
      ctx.stroke()
      ctx.fill()
      ctx.strokeStyle = 'white'
      ctx.fillStyle = 'white'
      ctx.x = 600
      ctx.closePath()
    }
    const renderCirle = () => {
      drawCircle(shape.x, shape.y, 50)
    }
    const render = (timestamp) => {
      if (null !== ctx) {
        if (shape.x < 600) {
          setDirection(DIRECTIONS['right'])
        } else if (shape.y > 100) {
          setDirection(DIRECTIONS['up'])
        }
        shape.x += direction[1]
        shape.y += direction[0]
        console.log(shape)
        renderCirle()
      }
      requestAnimationFrame(render)
    }
    setCanvas(canvasRef.current)
    setCtx(canvasRef.current.getContext('2d'))
    requestAnimationFrame(render)
  }, [canvas, ctx, direction]);
  return (
    <div>
      <canvas
        ref={canvasRef}
        width={window.innerWidth}
        height={window.innerHeight}
        onClick={e => {
          let x = e.clientX
          let y = e.clientY
          let coor = [x, y]
          console.log(coor)
        }}>
      </canvas>
    </div>
  )
}

const Bakground = (props) => {
  const renderFigure = () => <Canvas />
  return (
    <div>
      {renderFigure()}
      {props.children}
    </div>
  )
}

function App() {
  return (
    <div className="App container-fluid ml-lg-4">
      <Bakground>
        <img alt="PiÃ¨ce" src={coin} class="svg-2pomhL coin2-2eQxxG float-21_26k" />
        <Nav />
        <Lobby />
        <Footer /></Bakground>
    </div>
  );
}

export default App;
